sys_transform_script.script:
var QP = request.queryParams;

if(!(source.u_manager)){
	var str = "Starting: ";

	var grp = new GlideRecord('sys_user_group');
	grp.addQuery('name',source.u_support_team);
	grp.query();

	if(grp.next()){
		var manS = grp.getValue('manager');
		var man = new GlideRecord('sys_user');
		man.addQuery('sys_id',manS);
		man.query();

		if(man.next()){
			source.u_manager = man.name;
		}
	}
}

if(!(source.u_application)){
	var apps = new GlideRecord('cmdb_ci_business_app');
	apps.addQuery('it_application_owner', '=', manS);
	apps.query();

	if(apps.next()){
		source.u_application = apps.getValue('name');
	}
}
